        <span class="token function">resolve</span><span class="token punctuation">(</span><span class="token function">sourceDataMock</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span>list<span class="token punctuation">)</span><span class="token punctuation">;</span>
